var assert = require('assert');
var say    = require('../lib/say');

describe('say', function() {
  describe('cheese', function () {
    it('should say "Say cheese!"', function () {
      assert.equal('Say cheese!', say.cheese());
    });
    it('should say "Say ok!"', function () {
      assert.equal('Say ok!', say.cheese('ok'));
    });
  });
});
